object ExampleScala {
  def calc(p: Int) = {
    var result = 0
    for {
      a <- 3 to p
      b <- 1 until ((p - a) / 2)
      c = p - (a + b)
    } if (b * b + c * c == a * a) result += 1
    result
  }
}

public class ExampleJava {
  public static int calc(int p) {
    int result = 0;
    for (int a = 3; a <= p; a++) {
      for (int b = 1; b <= ((p - a) / 2); b++) {
        int c = p - (a + b);
        if (b * b + c * c == a * a) result++;
      }
    }
    return result;
  }
}

object ExampleScala {
  def calcWithWhile(p: Int) = {
    var (result, a) = (0, 3)
    while (a <= p) {
      var b = 1
      while (b < ((p - a) / 2)) {
        val c = p - (a + b)
        if (b * b + c * c == a * a)
          result += 1
          b += 1
      }
      a += 1
    }
    result
  }
}

