object ExampleScala {
  import spire.syntax.cfor._
  def calcWithCfor(p: Int) = {
    var result = 0
    cforRange(3 to p) { a =>
      cforRange(1 until ((p-a)/2)) { b =>
        val c = p-(a+b)
        if(b*b + c*c == a*a) result += 1
      }
    }
    result
  }
